private static final Map<String, SecureUserStore.User> USERS = new HashMap<>();

static {
    addUser("jack", "changeit", List.of("user", "admin"));
    addUser("jill", "changeit", List.of("user"));
    addUser("john", "changeit", List.of());
}

private static void addUser(String user, String password, List<String> roles) {
    USERS.put(user, new SecureUserStore.User() {
        @Override
        public String login() {
            return user;
        }

        char[] password() {
            return password.toCharArray();
        }

        @Override
        public boolean isPasswordValid(char[] password) {
            return Arrays.equals(password(), password);
        }

        @Override
        public Collection<String> roles() {
            return roles;
        }
    });
}

private static void routing2(HttpRouting.Builder routing) { SecurityHttpFeature security = SecurityHttpFeature.create(security2()) .securityDefaults(SecurityFeature.authenticate());
routing.addFeature(security) .get("/service2*", SecurityFeature.rolesAllowed("user")) .register(new Service2()); }
private static Security security2() { return Security.builder() .providerSelectionPolicy(CompositeProviderSelectionPolicy .builder() .addAuthenticationProvider("http-signatures", CompositeProviderFlag.OPTIONAL) .addAuthenticationProvider("basic-auth") .build()) .addProvider(HttpBasicAuthProvider .builder() .realm("mic") .userStore(users()), "basic-auth") .addProvider(HttpSignProvider.builder() .addInbound(InboundClientDefinition .builder("service1-hmac") .principalName("Service1 - HMAC signature") .hmacSecret("changeit") .build()) .addInbound(InboundClientDefinition .builder("service1-rsa") .principalName("Service1 - RSA signature") .publicKeyConfig(Keys.builder() .keystore(k -> k .keystore(Resource.create("keystore.p12")) .passphrase("changeit") .certAlias("service_cert") .build()) .build()) .build()), "http-signatures") .build(); }
private static Security security1() { return Security.builder() .providerSelectionPolicy(CompositeProviderSelectionPolicy .builder() .addOutboundProvider("basic-auth") .addOutboundProvider("http-signatures") .build()) .addProvider(HttpBasicAuthProvider .builder() .realm("mic") .userStore(users()) .addOutboundTarget(OutboundTarget.builder("propagate-all").build()), "basic-auth") .addProvider(HttpSignProvider .builder() .outbound(OutboundConfig .builder() .addTarget(hmacTarget()) .addTarget(rsaTarget()) .build()), "http-signatures") .build(); }
private static OutboundTarget rsaTarget() { return OutboundTarget.builder("service2-rsa") .addHost("localhost") .addPath("/service2-rsa.*") .customObject(OutboundTargetDefinition.class, OutboundTargetDefinition.builder("service1-rsa") .privateKeyConfig(Keys.builder() .keystore(k -> k .keystore(Resource.create("keystore.p12")) .passphrase("changeit") .keyAlias("myPrivateKey") .build()) .build()) .build()) .build(); }
private static OutboundTarget hmacTarget() { return OutboundTarget.builder("service2") .addHost("localhost") .addPath("/service2") .customObject( OutboundTargetDefinition.class, OutboundTargetDefinition .builder("service1-hmac") .hmacSecret("changeit") .build()) .build(); }
private static SecureUserStore users() { return login -> Optional.ofNullable(USERS.get(login)); }
