private static final JsonObject TEST_JSON_OBJECT = JSON_BUILDER.createObjectBuilder()
            .add("greeting", "Hola")
            .build();

@Test
void testGreetings() {
    JsonObject jsonObject;
    WebClientResponse response;

    jsonObject = webClient.get()
            .path("/greet/Joe")
            .request(JsonObject.class)
            .await(Duration.ofSeconds(5));
    assertThat(jsonObject.getString("message"), is("Hello Joe!"));

    response = webClient.put()
            .path("/greet/greeting")
            .submit(TEST_JSON_OBJECT)
            .await(Duration.ofSeconds(5));
    assertThat(response.status().code(), is(204));

    jsonObject = webClient.get()
            .path("/greet/Joe")
            .request(JsonObject.class)
            .await(Duration.ofSeconds(5));
    assertThat(jsonObject.getString("message"), is("Hola Joe!"));
}

@Test
void testGreetings() {
    Message json;
    WebClientResponse response;

    json = webClient.get()
            .path("/greet/Joe")
            .request(Message.class)
            .await(Duration.ofSeconds(5));
    assertThat(json.getMessage(), is("Hello Joe!"));

    response = webClient.put()
            .path("/greet/greeting")
            .submit("{\"greeting\" : \"Hola\"}")
            .await(Duration.ofSeconds(5));
    assertThat(response.status().code(), is(204));

    json = webClient.get()
            .path("/greet/Joe")
            .request(Message.class)
            .await(Duration.ofSeconds(5));
    assertThat(json.getMessage(), is("Hola Joe!"));
}
